$(async()=>{$('#link').hide();$('#generate').on('click',()=>{const input=$('#search-bar').val();if(!(/\w+/g.test(input))){return notify('fa fa-ban','danger','Invalid input!')}notify('fa fa-check','success','Generated link.');$('#link').val(location.href+encodeURI(input.split(' ').join('+'))).show(500)});$('#link').on('click',()=>{const input=$('#link').val();console.log('clicked');if(!(/\w+/g.test(input))){return}var copyText=document.getElementById('link');copyText.select();copyText.setSelectionRange(0,99999);document.execCommand('copy');notify('fa fa-check','success','Copied to clipboard.')})});function notify(icon,type,message){$.notify({icon,message},{type});return}