$(()=>{const proxyUrl="https://cors-anywhere.herokuapp.com/",links=["https://insult.mattbas.org/api/insult.json?who="+encodeURI(user)];$("#insult").hide();$("button#generate").on("click",async t=>{$("button#generate").prop("disabled",!0);await fetch(proxyUrl+links[0]).then(t=>t.json()).then(t=>$("#insult").show(500).val(t.insult+"."));$("button#generate").prop("disabled",!1)});$('#insult').on('click',()=>{const input=$('#insult').val();console.log('clicked');if(!(/\w+/g.test(input))){return}console.log('clicked');var copyText=document.getElementById('insult');console.log(copyText);copyText.select();copyText.setSelectionRange(0,99999);document.execCommand('copy');notify('fa fa-check','success','Copied to clipboard.')})});function notify(icon,type,message){$.notify({icon,message},{type});return}